# --- ‡¶á‡¶â‡¶ú‡¶æ‡¶∞ ‡¶∏‡ßç‡¶ü‡ßá‡¶ü ‡¶Æ‡ßç‡¶Ø‡¶æ‡¶®‡ßá‡¶ú‡¶Æ‡ßá‡¶®‡ßç‡¶ü ---
user_states = {}

# --- ‡¶ï‡¶Æ‡¶æ‡¶®‡ßç‡¶° ‡¶π‡ßç‡¶Ø‡¶æ‡¶®‡ßç‡¶°‡¶≤‡¶æ‡¶∞ ---
@bot.message_handler(commands=['start'])
def send_welcome(message):
    chat_id = message.chat.id
    user_states[chat_id] = {'current_q': 0, 'score': 0}
    
    markup = types.InlineKeyboardMarkup()
    start_btn = types.InlineKeyboardButton("üöÄ ‡¶™‡¶∞‡ßÄ‡¶ï‡ßç‡¶∑‡¶æ ‡¶∂‡ßÅ‡¶∞‡ßÅ ‡¶ï‡¶∞‡ßÅ‡¶®", callback_data="start_quiz")
    markup.add(start_btn)
    
    bot.send_message(chat_id, "‡¶∏‡ßç‡¶¨‡¶æ‡¶ó‡¶§‡¶Æ! ‡¶´‡¶ø‡¶ú‡¶ø‡¶ï‡ßç‡¶∏ ‡¶ï‡ßÅ‡¶á‡¶ú ‡¶ü‡ßá‡¶∏‡ßç‡¶ü‡ßá ‡¶Ü‡¶™‡¶®‡¶æ‡¶ï‡ßá ‡¶∏‡ßç‡¶¨‡¶æ‡¶ó‡¶§‡¶Æ‡•§\n‡¶Æ‡ßã‡¶ü ‡¶™‡ßç‡¶∞‡¶∂‡ßç‡¶®: ‡ß©‡ß´‡¶ü‡¶ø\n‡¶∂‡ßÅ‡¶∞‡ßÅ ‡¶ï‡¶∞‡¶§‡ßá ‡¶®‡¶ø‡¶ö‡ßá‡¶∞ ‡¶¨‡¶æ‡¶ü‡¶®‡ßá ‡¶ï‡ßç‡¶≤‡¶ø‡¶ï ‡¶ï‡¶∞‡ßÅ‡¶®‡•§", reply_markup=markup)

# --- ‡¶™‡ßç‡¶∞‡¶∂‡ßç‡¶® ‡¶™‡¶æ‡¶†‡¶æ‡¶®‡ßã‡¶∞ ‡¶´‡¶æ‡¶Ç‡¶∂‡¶® ---
def send_question(chat_id):
    if chat_id not in user_states:
        user_states[chat_id] = {'current_q': 0, 'score': 0}
        
    user_data = user_states[chat_id]
    q_index = user_data['current_q']
    
    if q_index >= len(quiz_data):
        score = user_data['score']
        total = len(quiz_data)
        percentage = (score / total) * 100
        
        # ‡¶´‡¶≤‡¶æ‡¶´‡¶≤ ‡¶Æ‡ßá‡¶∏‡ßá‡¶ú
        result_text = f"‚úÖ *‡¶™‡¶∞‡ßÄ‡¶ï‡ßç‡¶∑‡¶æ ‡¶∂‡ßá‡¶∑!*\n\n"
        result_text += f"üìä ‡¶Ü‡¶™‡¶®‡¶æ‡¶∞ ‡¶∏‡ßç‡¶ï‡ßã‡¶∞: {score}/{total}\n"
        result_text += f"üìà ‡¶™‡ßç‡¶∞‡¶æ‡¶™‡ßç‡¶§ ‡¶®‡¶Æ‡ßç‡¶¨‡¶∞: {percentage:.2f}%\n\n"
        
        if percentage >= 80:
            result_text += "üèÜ ‡¶¶‡¶æ‡¶∞‡ßÅ‡¶£ ‡¶™‡¶æ‡¶∞‡¶´‡¶∞‡¶Æ‡ßç‡¶Ø‡¶æ‡¶®‡ßç‡¶∏!"
        elif percentage >= 50:
            result_text += "üëç ‡¶≠‡¶æ‡¶≤‡ßã ‡¶π‡ßü‡ßá‡¶õ‡ßá, ‡¶Ü‡¶∞‡¶ì ‡¶ö‡ßá‡¶∑‡ßç‡¶ü‡¶æ ‡¶ï‡¶∞‡ßÅ‡¶®‡•§"
        else:
            result_text += "‚ö†Ô∏è ‡¶Ü‡¶™‡¶®‡¶æ‡¶ï‡ßá ‡¶Ü‡¶∞‡¶ì ‡¶™‡ßú‡¶§‡ßá ‡¶π‡¶¨‡ßá‡•§"
            
        markup = types.InlineKeyboardMarkup()
        restart_btn = types.InlineKeyboardButton("‡¶™‡ßÅ‡¶®‡¶∞‡¶æ‡ßü ‡¶™‡¶∞‡ßÄ‡¶ï‡ßç‡¶∑‡¶æ ‡¶¶‡¶ø‡¶®", callback_data="restart_quiz")
        markup.add(restart_btn)
        
        bot.send_message(chat_id, result_text, parse_mode="Markdown", reply_markup=markup)
        del user_states[chat_id]
        return

    question_data = quiz_data[q_index]
    question_text = f"‡¶™‡ßç‡¶∞‡¶∂‡ßç‡¶® {q_index + 1}/{len(quiz_data)}:\n\n{question_data['question']}"
    options = question_data['options']

    markup = types.InlineKeyboardMarkup()
    for i, option in enumerate(options):
        markup.add(types.InlineKeyboardButton(option, callback_data=f"ans_{i}"))

    bot.send_message(chat_id, question_text, reply_markup=markup)

# --- ‡¶¨‡¶æ‡¶ü‡¶® ‡¶π‡ßç‡¶Ø‡¶æ‡¶®‡ßç‡¶°‡¶≤‡¶æ‡¶∞ ---
@bot.callback_query_handler(func=lambda call: True)
def handle_query(call):
    chat_id = call.message.chat.id
    
    if call.data == "start_quiz":
        send_question(chat_id)
        
    elif call.data == "restart_quiz":
        user_states[chat_id] = {'current_q': 0, 'score': 0}
        send_question(chat_id)
        
    elif call.data.startswith("ans_"):
        if chat_id not in user_states:
            bot.send_message(chat_id, "‚ö†Ô∏è ‡¶∏‡ßá‡¶∂‡¶® ‡¶∂‡ßá‡¶∑ ‡¶π‡ßü‡ßá ‡¶ó‡ßá‡¶õ‡ßá‡•§ ‡¶¶‡ßü‡¶æ ‡¶ï‡¶∞‡ßá /start ‡¶ö‡¶æ‡¶™‡ßÅ‡¶®‡•§")
            return

        selected_index = int(call.data.split("_")[1])
        user_data = user_states[chat_id]
        q_index = user_data['current_q']
        
        correct_index = quiz_data[q_index]['correct_index']
        
        if selected_index == correct_index:
            user_states[chat_id]['score'] += 1
            bot.answer_callback_query(call.id, "‚úÖ ‡¶∏‡¶†‡¶ø‡¶ï ‡¶â‡¶§‡ßç‡¶§‡¶∞!")
        else:
            correct_ans_text = quiz_data[q_index]['options'][correct_index]
            bot.answer_callback_query(call.id, "‚ùå ‡¶≠‡ßÅ‡¶≤ ‡¶â‡¶§‡ßç‡¶§‡¶∞!")
            bot.send_message(chat_id, f"‚ùå ‡¶≠‡ßÅ‡¶≤ ‡¶â‡¶§‡ßç‡¶§‡¶∞!\n‡¶∏‡¶†‡¶ø‡¶ï ‡¶â‡¶§‡ßç‡¶§‡¶∞‡¶ü‡¶ø ‡¶π‡¶≤‡ßã: {correct_ans_text}")

        user_states[chat_id]['current_q'] += 1
        send_question(chat_id)

